import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { socialMediaChannelsAPI, SocialMediaChannel } from '../../api/socialMediaChannels';
import { 
  ArrowLeft, RefreshCw, Users, MessageSquare, Send, 
  Paperclip, X, Image as ImageIcon, Video, FileText, 
  CheckCircle, XCircle, Trash2, Download, UserPlus, 
  Building2, BarChart3, TrendingUp, Bot
} from 'lucide-react';
import toast from 'react-hot-toast';
import api from '../../utils/api';
import { format } from 'date-fns';
import { AssignUsersModal } from '../../components/channels/AssignUsersModal';

interface Message {
  id: number;
  message_text: string;
  is_sent: boolean;
  timestamp: string;
  media_url?: string | null;
  message_type?: string | null;
  file_name?: string | null;
  is_sending?: boolean;
  temp_id?: number;
}

interface ChannelStatistics {
  total_messages: number;
  sent_posts: number;
  total_members: number;
  assigned_users_count: number;
}

interface BotInfo {
  id: number;
  name: string;
  username: string;
  is_active: boolean;
  is_verified: boolean;
}

interface AssignedUserStats {
  id: number;
  name: string;
  email: string;
  sent_messages: number;
}

const ChannelDetailOptimized: React.FC = () => {
  const { channelId } = useParams<{ channelId: string }>();
  const navigate = useNavigate();
  const { user, loading: authLoading } = useAuth();

  // State
  const [channel, setChannel] = useState<SocialMediaChannel | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [loading, setLoading] = useState(true);
  const [showAssignUsersModal, setShowAssignUsersModal] = useState(false);
  const [assignedUsers, setAssignedUsers] = useState<any[]>([]);

  // Message input
  const [messageText, setMessageText] = useState('');
  const [mediaFiles, setMediaFiles] = useState<File[]>([]);
  const [isSending, setIsSending] = useState(false);

  // Refs
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const pollIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const lastMessageCountRef = useRef<number>(0);

  // Scroll to bottom
  const scrollToBottom = useCallback(() => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  }, []);

  // Load channel
  const loadChannel = useCallback(async () => {
    if (!channelId) return;

    try {
      const data = await socialMediaChannelsAPI.getChannel(Number(channelId));
      setChannel(data);
    } catch (error: any) {
      toast.error('Kanal bilgileri yüklenemedi');
      navigate('/admin/social-media/channels');
    }
  }, [channelId, navigate]);

  // Load assigned users
  const loadAssignedUsers = useCallback(async () => {
    if (!channelId) return;

    try {
      const response = await socialMediaChannelsAPI.getChannelAssignedUsers(Number(channelId));
      setAssignedUsers(response.assigned_users || []);
    } catch (error) {
      // Silently handle error - assigned users are not critical
    }
  }, [channelId]);

  // Load messages
  const loadMessages = useCallback(async (silent = false) => {
    if (!channelId) return;

    try {
      if (!silent) setLoading(true);

      const response = await api.get(`/api/social-media/channels/${channelId}/messages`, {
        params: { per_page: 500 }
      });

      const fetchedMessages: Message[] = response.data.messages || [];

      // Check for new messages
      const hasNewMessages = fetchedMessages.length > lastMessageCountRef.current;
      lastMessageCountRef.current = fetchedMessages.length;

      setMessages(fetchedMessages);

      // Auto-scroll on new messages
      if (hasNewMessages) {
        scrollToBottom();
      }
    } catch (error: any) {
      if (!silent) toast.error('Mesajlar yüklenemedi');
    } finally {
      setLoading(false);
    }
  }, [channelId, scrollToBottom]);

  // Initial load
  useEffect(() => {
    if (authLoading) return;
    if (!user) {
      navigate('/login', { replace: true });
      return;
    }

    loadChannel();
    loadAssignedUsers();
    loadMessages();
  }, [authLoading, user, navigate, loadChannel, loadAssignedUsers, loadMessages]);

  // Real-time polling (every 3 seconds)
  useEffect(() => {
    if (!channelId) return;

    pollIntervalRef.current = setInterval(() => {
      loadMessages(true);
    }, 3000);

    return () => {
      if (pollIntervalRef.current) {
        clearInterval(pollIntervalRef.current);
      }
    };
  }, [channelId, loadMessages]);

  // File handling
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!e.target.files) return;

    const files = Array.from(e.target.files);
    if (files.length + mediaFiles.length > 50) {
      toast.error('En fazla 50 dosya ekleyebilirsiniz');
      return;
    }

    setMediaFiles(prev => [...prev, ...files]);
  };

  const removeFile = (index: number) => {
    setMediaFiles(prev => prev.filter((_, i) => i !== index));
  };

  const clearAllFiles = () => {
    setMediaFiles([]);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Send message
  const handleSendMessage = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!channelId || (!messageText.trim() && mediaFiles.length === 0)) return;
    if (isSending) return;

    setIsSending(true);

    try {
      const textToSend = messageText.trim();
      const filesToSend = [...mediaFiles];

      // Clear inputs immediately for better UX
      setMessageText('');
      clearAllFiles();

      // Send messages
      if (filesToSend.length > 0) {
        // Multiple files - send directly with FormData
        for (let i = 0; i < filesToSend.length; i++) {
          const file = filesToSend[i];

          try {
            // Direkt FormData ile dosya gönder
            const formData = new FormData();
            formData.append('file', file);
            formData.append('message_text', textToSend || file.name);

            // Yeni upload endpoint'ini kullan
            await api.post(
              `/api/social-media/channels/${channelId}/messages/upload`,
              formData,
              {
                headers: { 'Content-Type': 'multipart/form-data' }
              }
            );

            // Rate limiting için kısa bekleme
            if (i < filesToSend.length - 1) {
              await new Promise(resolve => setTimeout(resolve, 500));
            }

          } catch (error: any) {
            toast.error(`Dosya ${i+1} gönderilemedi: ${file.name}`);
          }
        }

        toast.success(`${filesToSend.length} dosya gönderildi!`);
      } else {
        // Text only
        await api.post(`/api/social-media/channels/${channelId}/messages`, {
          message_text: textToSend,
          message_type: 'text'
        });

        toast.success('Mesaj gönderildi!');
      }

      // Reload messages after 2 seconds
      setTimeout(() => loadMessages(true), 2000);

    } catch (error: any) {
      toast.error(error.message || 'Mesaj gönderilemedi');
    } finally {
      setIsSending(false);
    }
  };

  // Sync from Telegram
  const handleTelegramSync = async () => {
    try {
      toast.loading('Telegram mesajları senkronize ediliyor...', { duration: 2000 });
      await api.post(`/api/social-media/channels/${channelId}/sync-messages`);
      toast.success('Mesajlar senkronize ediliyor');
      setTimeout(() => loadMessages(true), 3000);
    } catch (error) {
      toast.error('Senkronizasyon başarısız');
    }
  };

  // Clear all messages
  const handleClearMessages = async () => {
    if (!window.confirm('Tüm mesajları silmek istediğinizden emin misiniz?')) return;

    try {
      toast.loading('Mesajlar temizleniyor...', { id: 'clear' });
      const response = await api.delete(`/api/social-media/channels/${channelId}/messages/clear`);
      setMessages([]);
      lastMessageCountRef.current = 0;
      toast.success(`✅ ${response.data.deleted_count} mesaj temizlendi!`, { id: 'clear' });
    } catch (error: any) {
      toast.error('Mesajlar temizlenemedi', { id: 'clear' });
    }
  };

  // Render file icon
  const renderFileIcon = (type?: string | null) => {
    if (type === 'image') return <ImageIcon size={16} className="text-blue-500" />;
    if (type === 'video') return <Video size={16} className="text-purple-500" />;
    return <FileText size={16} className="text-gray-500" />;
  };

  // Loading states
  if (authLoading || loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!channel) return null;

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      {/* Header - Compact with Full Info */}
      <div className="bg-white border-b border-gray-200 px-4 py-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <button
              onClick={() => navigate('/admin/social-media/channels')}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <ArrowLeft size={20} />
            </button>

            <div>
              <h1 className="text-lg font-semibold text-gray-900">{channel.name}</h1>
              <div className="flex items-center gap-3 text-xs text-gray-500">
                <span className="flex items-center gap-1">
                  <MessageSquare size={12} />
                  {messages.length} mesaj
                </span>
                <span className="flex items-center gap-1">
                  <Users size={12} />
                  {channel.member_count || 0} üye
                </span>
                <span className="flex items-center gap-1">
                  <Building2 size={12} />
                  {channel.brand_name || 'Marka'}
                </span>
                {channel.is_active ? (
                  <span className="flex items-center gap-1 text-green-600">
                    <CheckCircle size={12} />
                    Aktif
                  </span>
                ) : (
                  <span className="flex items-center gap-1 text-red-600">
                    <XCircle size={12} />
                    Pasif
                  </span>
                )}
              </div>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {(user?.permissions?.includes('social.manage') || user?.permissions?.includes('brands.manage')) && (
              <button
                onClick={() => setShowAssignUsersModal(true)}
                className="flex items-center gap-2 px-3 py-1.5 text-sm bg-purple-50 text-purple-600 hover:bg-purple-100 rounded-lg transition-colors"
                title="Kullanıcı Yönet"
              >
                <UserPlus size={14} />
                Kullanıcı ({assignedUsers.length})
              </button>
            )}

            <button
              onClick={handleTelegramSync}
              className="flex items-center gap-2 px-3 py-1.5 text-sm bg-blue-50 text-blue-600 hover:bg-blue-100 rounded-lg transition-colors"
              title="Telegram'dan Mesajları Çek"
            >
              <RefreshCw size={14} />
              Sync
            </button>

            {(user?.role === 'Super Admin' || user?.role === 'Mağaza Yöneticisi') && messages.length > 0 && (
              <button
                onClick={handleClearMessages}
                className="flex items-center gap-2 px-3 py-1.5 text-sm bg-red-50 text-red-600 hover:bg-red-100 rounded-lg transition-colors"
                title="Tüm mesajları temizle"
              >
                <Trash2 size={14} />
                Temizle
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Assigned Users Display */}
      {assignedUsers.length > 0 && (
        <div className="bg-gray-50 border-b border-gray-200 px-4 py-2">
          <div className="flex items-center gap-2">
            <span className="text-xs text-gray-600">Atanan Kullanıcılar:</span>
            <div className="flex flex-wrap gap-1">
              {assignedUsers.map((user: any) => (
                <span
                  key={user.id}
                  className="inline-flex items-center gap-1 px-2 py-0.5 bg-white rounded-full border border-gray-200 text-xs text-gray-700"
                >
                  <Users size={10} />
                  {user.first_name} {user.last_name}
                </span>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Messages Area - Minimal Design */}
      <div className="flex-1 overflow-y-auto px-4 py-4 space-y-2">
        {messages.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full text-gray-400">
            <MessageSquare size={48} className="mb-3" />
            <p className="text-sm">Henüz mesaj yok</p>
            <p className="text-xs">Aşağıdan mesaj gönderin</p>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`flex ${message.is_sent ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-[70%] rounded-lg px-3 py-2 ${
                  message.is_sent
                    ? 'bg-blue-500 text-white'
                    : 'bg-white border border-gray-200 text-gray-900'
                } ${message.is_sending ? 'opacity-50' : ''}`}
              >
                {/* Media */}
                {message.media_url && (
                  <div className="mb-2">
                    {message.message_type === 'image' && (
                      <img
                        src={message.media_url}
                        alt={message.file_name || 'Image'}
                        className="max-w-full max-h-48 rounded cursor-pointer"
                        onClick={() => window.open(message.media_url!, '_blank')}
                      />
                    )}
                    {message.message_type === 'video' && (
                      <video
                        src={message.media_url}
                        controls
                        className="max-w-full max-h-48 rounded"
                      />
                    )}
                    {message.message_type === 'document' && (
                      <a
                        href={message.media_url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center gap-2 text-sm hover:underline"
                      >
                        <Download size={14} />
                        {message.file_name || 'Dosya'}
                      </a>
                    )}
                  </div>
                )}

                {/* Text */}
                {message.message_text && (
                  <p className="text-sm break-words">{message.message_text}</p>
                )}

                {/* Timestamp */}
                <div className={`text-xs mt-1 ${message.is_sent ? 'text-blue-100' : 'text-gray-400'}`}>
                  {format(new Date(message.timestamp), 'HH:mm')}
                </div>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* File Previews - Minimal */}
      {mediaFiles.length > 0 && (
        <div className="px-4 py-2 bg-gray-100 border-t border-gray-200">
          <div className="flex items-center gap-2 flex-wrap">
            <span className="text-xs text-gray-600">{mediaFiles.length} dosya:</span>
            {mediaFiles.map((file, index) => (
              <div
                key={index}
                className="flex items-center gap-1 px-2 py-1 bg-white rounded border border-gray-300 text-xs"
              >
                {renderFileIcon(file.type.split('/')[0])}
                <span className="max-w-[100px] truncate">{file.name}</span>
                <button
                  onClick={() => removeFile(index)}
                  className="ml-1 text-red-500 hover:text-red-700"
                >
                  <X size={12} />
                </button>
              </div>
            ))}
            <button
              onClick={clearAllFiles}
              className="text-xs text-red-600 hover:text-red-700 underline"
            >
              Tümünü Kaldır
            </button>
          </div>
        </div>
      )}

      {/* Input Area - Compact */}
      <div className="bg-white border-t border-gray-200 px-4 py-3">
        <form onSubmit={handleSendMessage} className="flex items-end gap-2">
          <input
            ref={fileInputRef}
            type="file"
            multiple
            accept="image/*,video/*,.pdf,.doc,.docx,.txt"
            onChange={handleFileChange}
            className="hidden"
          />

          <button
            type="button"
            onClick={() => fileInputRef.current?.click()}
            className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors"
            disabled={isSending}
          >
            <Paperclip size={20} />
          </button>

          <textarea
            value={messageText}
            onChange={(e) => setMessageText(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(e);
              }
            }}
            placeholder="Mesajınızı yazın..."
            className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
            rows={1}
            disabled={isSending}
          />

          <button
            type="submit"
            disabled={isSending || (!messageText.trim() && mediaFiles.length === 0)}
            className="p-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <Send size={20} />
          </button>
        </form>
      </div>

      {/* Assign Users Modal */}
      {showAssignUsersModal && channel && user && (
        <AssignUsersModal
          channelId={channel.id}
          channelName={channel.name}
          brandId={channel.brand_id}
          currentAssignedUserIds={channel.assigned_user_ids || []}
          currentUserId={user.id}
          onClose={() => setShowAssignUsersModal(false)}
          onSuccess={() => {
            loadAssignedUsers();
            loadChannel();
          }}
        />
      )}
    </div>
  );
};

export default ChannelDetailOptimized;
