import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { useTableData } from '../../hooks/useTableData';
import { socialMediaChannelsAPI, SocialMediaChannel } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { SearchBar } from '../../components/common/SearchBar';
import { FilterDropdown } from '../../components/common/FilterDropdown';
import { Pagination } from '../../components/common/Pagination';
import DynamicChannelModal from '../../components/modals/DynamicChannelModal';
import { 
  MessageCircle, Plus, Edit, Trash2, ToggleLeft, ToggleRight,
  Users, Activity, CheckCircle, XCircle, RefreshCw, Eye
} from 'lucide-react';
import toast from 'react-hot-toast';

const SocialMediaChannelsOptimized: React.FC = () => {
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  
  const [showModal, setShowModal] = useState(false);
  const [editingChannel, setEditingChannel] = useState<SocialMediaChannel | null>(null);
  const [selectedPlatform, setSelectedPlatform] = useState<'telegram' | null>(null);
  const [brands, setBrands] = useState<any[]>([]);
  const [statistics, setStatistics] = useState<any>(null);
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Stable fetch function using useCallback
  const fetchChannels = useCallback(async ({ page, perPage, search, filters }: any) => {
    const response = await socialMediaChannelsAPI.getChannels(
      page,
      perPage,
      undefined, // platform - not needed
      filters?.brand || undefined,
      search
    );

    // Apply status filter on frontend
    let filteredChannels = response.channels;
    if (filters?.status && filters.status !== 'all') {
      filteredChannels = response.channels.filter((channel: SocialMediaChannel) => {
        if (filters?.status === 'active') return channel.is_active;
        if (filters?.status === 'inactive') return !channel.is_active;
        return true;
      });
    }

    return {
      data: filteredChannels,
      total: response.total,
      totalPages: response.total_pages
    };
  }, []); // Empty deps - function doesn't depend on anything

  // State for channels
  const [channels, setChannels] = useState<SocialMediaChannel[]>([]);
  const [channelsLoading, setChannelsLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize] = useState(12);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [brandFilter, setBrandFilter] = useState<number | null>(null);
  const [statusFilter, setStatusFilter] = useState<string>('all');
  
  // Load channels function
  const loadChannels = useCallback(async () => {
    try {
      setChannelsLoading(true);
      const result = await fetchChannels({
        page: currentPage,
        perPage: pageSize,
        search: searchTerm,
        filters: {
          brand: brandFilter,
          status: statusFilter
        }
      });
      
      setChannels(result.data);
      setTotalPages(result.totalPages);
    } catch (error) {
      console.error('Error loading channels:', error);
      toast.error('Kanallar yüklenemedi');
    } finally {
      setChannelsLoading(false);
    }
  }, [currentPage, pageSize, searchTerm, brandFilter, statusFilter, fetchChannels]);
  
  // Load channels on mount and filter changes
  useEffect(() => {
    loadChannels();
  }, [loadChannels]);
  
  // Reset to page 1 when filters change
  useEffect(() => {
    if (currentPage !== 1) {
      setCurrentPage(1);
    } else {
      loadChannels();
    }
  }, [searchTerm, brandFilter, statusFilter]);
  
  const resetTable = useCallback(() => {
    loadChannels();
  }, [loadChannels]);

  // Load brands
  const loadBrands = useCallback(async () => {
    try {
      const response = await brandsAPI.getBrands(1, 100);
      setBrands(response.brands);
    } catch (error) {
      // Silently handle error - brands are not critical
    }
  }, []);

  // Load statistics
  const loadStatistics = useCallback(async () => {
    try {
      const stats = await socialMediaChannelsAPI.getStatistics();
      setStatistics(stats);
    } catch (error) {
      // Silently handle error - statistics are not critical
    }
  }, []);

  // Initial load
  useEffect(() => {
    loadBrands();
    loadStatistics();
  }, [loadBrands, loadStatistics]);

  // Auth check - Prevent redirect loop
  useEffect(() => {
    if (authLoading) return; // Still loading
    
    if (!user) {
      navigate('/login', { replace: true }); // Replace history to prevent back button issues
    }
  }, [authLoading, user, navigate]);

  // Auto-refresh
  useEffect(() => {
    if (!autoRefresh) return;

    const interval = setInterval(() => {
      resetTable();
      loadStatistics();
    }, 30000); // 30 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, resetTable, loadStatistics]);

  // Handle delete
  const handleDeleteChannel = async (channelId: number) => {
    if (!window.confirm('Bu kanalı silmek istediğinizden emin misiniz?')) return;

    try {
      await socialMediaChannelsAPI.deleteChannel(channelId);
      toast.success('Kanal silindi');
      resetTable();
      await loadStatistics();
    } catch (error: any) {
      toast.error(error?.response?.data?.detail || 'Kanal silinemedi');
    }
  };

  // Handle toggle status
  const handleToggleChannel = async (channelId: number) => {
    try {
      await socialMediaChannelsAPI.toggleChannelStatus(channelId);
      toast.success('Kanal durumu güncellendi');
      resetTable();
      await loadStatistics();
    } catch (error: any) {
      toast.error('Durum güncellenemedi');
    }
  };

  // Handle refresh channel info
  const handleRefreshChannelInfo = async (channelId: number) => {
    try {
      toast.loading('Kanal bilgileri güncelleniyor...', { id: 'refresh' });
      await socialMediaChannelsAPI.refreshChannelInfo(channelId);
      resetTable();
      toast.success('Kanal bilgileri güncellendi', { id: 'refresh' });
    } catch (error: any) {
      toast.error('Bilgiler güncellenemedi', { id: 'refresh' });
    }
  };

  // Handle modal success
  const handleModalSuccess = async () => {
    setShowModal(false);
    setEditingChannel(null);
    setSelectedPlatform(null);
    resetTable();
    await loadStatistics();
  };

  // Memoized stats cards
  const statsCards = useMemo(() => {
    if (!statistics) return [];

    return [
      {
        title: 'Toplam Kanal',
        value: statistics.total_channels || 0,
        icon: MessageCircle,
        color: 'blue'
      },
      {
        title: 'Aktif Kanallar',
        value: statistics.active_channels || 0,
        icon: CheckCircle,
        color: 'green'
      },
      {
        title: 'Toplam Üye',
        value: statistics.total_members || 0,
        icon: Users,
        color: 'purple'
      },
      {
        title: 'Toplam Mesaj',
        value: statistics.total_messages || 0,
        icon: Activity,
        color: 'orange'
      }
    ];
  }, [statistics]);

  // Brand filter options
  const brandFilterOptions = useMemo(() => {
    return [
      { value: null, label: 'Tüm Markalar' },
      ...brands.map(brand => ({ value: brand.id, label: brand.name }))
    ];
  }, [brands]);

  // Status filter options
  const statusFilterOptions = [
    { value: 'all', label: 'Tümü' },
    { value: 'active', label: 'Aktif' },
    { value: 'inactive', label: 'Pasif' }
  ];

  // Show loading only while auth is loading
  if (authLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="inline-block animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  // Don't render anything if not authenticated (redirect will happen)
  if (!user) {
    return null;
  }

  return (
    <div className="space-y-6 p-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Sosyal Medya Kanalları</h1>
          <p className="text-gray-600 mt-1">Telegram kanallarınızı yönetin</p>
        </div>
        <button
          onClick={() => {
            setSelectedPlatform('telegram');
            setShowModal(true);
          }}
          className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          <Plus size={20} />
          Yeni Kanal
        </button>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {statsCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div key={index} className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">{stat.title}</p>
                  <p className="text-2xl font-bold text-gray-900 mt-1">{stat.value}</p>
                </div>
                <div className={`p-3 bg-${stat.color}-100 rounded-lg`}>
                  <Icon size={24} className={`text-${stat.color}-600`} />
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Filters */}
      <div className="card">
        <div className="flex flex-col md:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <SearchBar
              value={searchTerm}
              onChange={setSearchTerm}
              placeholder="Kanal ara..."
              debounceMs={300}
            />
          </div>

          {/* Status Filter */}
          <FilterDropdown
            label="Durum"
            value={statusFilter || 'all'}
            onChange={(value) => setStatusFilter(value as string)}
            options={statusFilterOptions}
          />

          {/* Brand Filter */}
          <FilterDropdown
            label="Marka"
            value={brandFilter?.toString() || null}
            onChange={(value) => setBrandFilter(value ? Number(value) : null)}
            options={brandFilterOptions}
          />

          {/* Auto-refresh */}
          <button
            onClick={() => setAutoRefresh(!autoRefresh)}
            className={`px-4 py-2 rounded-lg transition-colors flex items-center gap-2 ${
              autoRefresh
                ? 'bg-green-100 text-green-700 hover:bg-green-200'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            <RefreshCw size={16} className={autoRefresh ? 'animate-spin' : ''} />
            {autoRefresh ? 'Otomatik Yenileme' : 'Manuel'}
          </button>

          {/* Clear Filters */}
          {(searchTerm || statusFilter !== 'all' || brandFilter) && (
            <button
              onClick={() => {
                setSearchTerm('');
                setStatusFilter('all');
                setBrandFilter(null);
              }}
              className="px-4 py-2 bg-red-100 text-red-700 rounded-lg hover:bg-red-200 transition-colors"
            >
              Filtreleri Temizle
            </button>
          )}
        </div>
      </div>

      {/* Channels Grid */}
      {channelsLoading ? (
        <div className="flex items-center justify-center py-12">
          <div className="text-center">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500 mb-2"></div>
            <p className="text-gray-600">Kanallar yükleniyor...</p>
          </div>
        </div>
      ) : channels.length === 0 ? (
        <div className="card text-center py-12">
          <MessageCircle size={48} className="mx-auto text-gray-300 mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">Kanal Bulunamadı</h3>
          <p className="text-gray-600 mb-4">
            {searchTerm || statusFilter !== 'all' || brandFilter
              ? 'Arama kriterlerinize uygun kanal bulunamadı'
              : 'Henüz hiç kanal eklenmemiş'}
          </p>
          <button
            onClick={() => {
              setSelectedPlatform('telegram');
              setShowModal(true);
            }}
            className="inline-flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <Plus size={20} />
            İlk Kanalı Ekle
          </button>
        </div>
      ) : (
        <>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {channels.map((channel) => (
              <div
                key={channel.id}
                className={`card hover:shadow-lg transition-all ${
                  !channel.is_active ? 'opacity-60' : ''
                }`}
              >
                {/* Channel Header */}
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center gap-3">
                    <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${
                      channel.is_active ? 'bg-blue-100' : 'bg-gray-200'
                    }`}>
                      <MessageCircle
                        size={24}
                        className={channel.is_active ? 'text-blue-600' : 'text-gray-400'}
                      />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{channel.name}</h3>
                      <p className="text-sm text-gray-600">{channel.brand_name}</p>
                    </div>
                  </div>
                  {channel.is_active ? (
                    <CheckCircle size={20} className="text-green-500" />
                  ) : (
                    <XCircle size={20} className="text-red-500" />
                  )}
                </div>

                {/* Channel Stats */}
                <div className="grid grid-cols-2 gap-4 mb-4 py-3 border-y border-gray-100">
                  <div className="text-center">
                    <p className="text-2xl font-bold text-gray-900">{channel.member_count || 0}</p>
                    <p className="text-xs text-gray-600">Üye</p>
                  </div>
                  <div className="text-center">
                    <p className="text-2xl font-bold text-gray-900">
                      {channel.last_activity
                        ? new Date(channel.last_activity).toLocaleDateString('tr-TR', {
                            day: '2-digit',
                            month: '2-digit'
                          })
                        : '-'}
                    </p>
                    <p className="text-xs text-gray-600">Son Aktivite</p>
                  </div>
                </div>

                {/* Inactive Badge */}
                {!channel.is_active && (
                  <div className="mb-3 px-3 py-1.5 bg-yellow-100 text-yellow-800 rounded-lg text-center text-sm font-medium">
                    Mesaj Gönderimi Kapalı
                  </div>
                )}

                {/* Actions */}
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => navigate(`/admin/social-media/channels/${channel.id}`)}
                    className="flex-1 flex items-center justify-center gap-2 px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    <Eye size={16} />
                    Detay
                  </button>

                  <button
                    onClick={() => handleToggleChannel(channel.id)}
                    className={`p-2 rounded-lg transition-colors ${
                      channel.is_active
                        ? 'bg-yellow-100 text-yellow-600 hover:bg-yellow-200'
                        : 'bg-green-100 text-green-600 hover:bg-green-200'
                    }`}
                    title={channel.is_active ? 'Pasife Al' : 'Aktifleştir'}
                  >
                    {channel.is_active ? <ToggleRight size={16} /> : <ToggleLeft size={16} />}
                  </button>

                  <button
                    onClick={() => handleRefreshChannelInfo(channel.id)}
                    className="p-2 bg-gray-100 text-gray-600 hover:bg-gray-200 rounded-lg transition-colors"
                    title="Bilgileri Yenile"
                  >
                    <RefreshCw size={16} />
                  </button>

                  <button
                    onClick={() => {
                      setEditingChannel(channel);
                      setSelectedPlatform('telegram');
                      setShowModal(true);
                    }}
                    className="p-2 bg-gray-100 text-gray-600 hover:bg-gray-200 rounded-lg transition-colors"
                    title="Düzenle"
                  >
                    <Edit size={16} />
                  </button>

                  {user?.permissions?.includes('social.manage') && (
                    <button
                      onClick={() => handleDeleteChannel(channel.id)}
                      className="p-2 bg-red-100 text-red-600 hover:bg-red-200 rounded-lg transition-colors"
                      title="Sil"
                    >
                      <Trash2 size={16} />
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>

          {/* Pagination */}
          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </>
      )}

      {/* Modal */}
      {showModal && selectedPlatform && (
        <DynamicChannelModal
          isOpen={showModal}
          platform={selectedPlatform}
          editingChannel={editingChannel}
          onClose={() => {
            setShowModal(false);
            setEditingChannel(null);
            setSelectedPlatform(null);
          }}
          onSuccess={handleModalSuccess}
        />
      )}
    </div>
  );
};

export default SocialMediaChannelsOptimized;
