import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle, Bot, Hash, RefreshCw, Link } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { telegramBotsAPI, TelegramBot } from '../../api/telegramBots';
import toast from 'react-hot-toast';

interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [brands, setBrands] = useState<any[]>([]);
  const [selectedBotId, setSelectedBotId] = useState<number | null>(null);
  const [channelType, setChannelType] = useState<'public' | 'private'>('public');
  const [channelIdentifier, setChannelIdentifier] = useState('');
  const [channelUsername, setChannelUsername] = useState(''); // Yeni: Private iÃ§in username
  const [selectedBrandId, setSelectedBrandId] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [loadingBots, setLoadingBots] = useState(false);
  const [verifying, setVerifying] = useState(false);
  const [selectedBot, setSelectedBot] = useState<TelegramBot | null>(null);
  const [showInstructions, setShowInstructions] = useState(false); // Talimat gÃ¶ster
  const [showUsernameInput, setShowUsernameInput] = useState(false); // Username input gÃ¶ster
  const [joinResult, setJoinResult] = useState<any>(null);

  useEffect(() => {
    if (isOpen && platform) {
      loadBots();
      loadBrands();
      setChannelType('public');
      setChannelIdentifier('');
      setChannelUsername('');
      setShowInstructions(false);
      setShowUsernameInput(false);
      setJoinResult(null);
    }
  }, [isOpen, platform]);

  const loadBots = async () => {
    try {
      setLoadingBots(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots.filter(bot => bot.is_active));
    } catch (error) {
      console.error('Bot listesi yÃ¼klenirken hata:', error);
      toast.error('Bot listesi yÃ¼klenemedi');
    } finally {
      setLoadingBots(false);
    }
  };

  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Error loading brands:', error);
    }
  };

  const handleBotChange = (botId: number) => {
    setSelectedBotId(botId);
    const bot = bots.find(b => b.id === botId);
    setSelectedBot(bot || null);
  };

  const getPlaceholder = () => {
    if (channelType === 'public') {
      return '@kanaladi veya https://t.me/kanaladi';
    } else {
      return 'https://t.me/+AbCdEfGhIjK... (Davet Linki)';
    }
  };

  // Ä°lk adÄ±m - Kanal ekleme isteÄŸi (private iÃ§in talimat alÄ±r)
  const handleInitialAdd = async () => {
    if (!selectedBotId || !channelIdentifier.trim() || !selectedBrandId) {
      toast.error('LÃ¼tfen tÃ¼m alanlarÄ± doldurun');
      return;
    }

    setVerifying(true);
    const loadingToast = toast.loading('Ä°ÅŸlem baÅŸlatÄ±lÄ±yor...');

    try {
      const response = await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId,
        channel_identifier: channelIdentifier.trim(),
        brand_id: selectedBrandId
      });

      toast.dismiss(loadingToast);

      if (response.success) {
        toast.success('Kanal eklendi!');
        onSuccess();
        onClose();
      } else if (response.requires_verification) {
        setJoinResult(response);
        setShowInstructions(true);
        toast.info('TalimatlarÄ± takip edin');
      } else {
        toast.error(response.message);
      }
    } catch (error: any) {
      toast.dismiss(loadingToast);
      const errorMessage = error.response?.data?.detail || 'Hata oluÅŸtu';
      if (errorMessage.includes('private')) {
        toast.error('Private kanal iÃ§in talimatlar gÃ¶steriliyor. LÃ¼tfen botu manuel ekleyin.');
        setShowInstructions(true);
      } else {
        toast.error(errorMessage);
      }
    } finally {
      setVerifying(false);
    }
  };

  // Ä°kinci adÄ±m - Username ile doÄŸrulama
  const handleVerifyWithUsername = async () => {
    if (!channelUsername.trim()) {
      toast.error('Kanal username\'ini girin (Ã¶rn: @kanaladim)');
      return;
    }

    setVerifying(true);
    const loadingToast = toast.loading('DoÄŸrulanÄ±yor...');

    try {
      const response = await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId!,
        channel_identifier: channelIdentifier.trim(), // Invite link
        brand_id: selectedBrandId,
        channel_username: channelUsername.trim() // DoÄŸrulama username
      });

      toast.dismiss(loadingToast);

      if (response.success) {
        toast.success('Kanal doÄŸrulandÄ± ve eklendi!');
        onSuccess();
        onClose();
      } else {
        toast.error(response.message);
      }
    } catch (error: any) {
      toast.dismiss(loadingToast);
      toast.error(error.response?.data?.detail || 'DoÄŸrulama hatasÄ±');
    } finally {
      setVerifying(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (channelType === 'public') {
      await handleInitialAdd();
    } else {
      await handleInitialAdd(); // Private iÃ§in talimat gÃ¶sterir
    }
  };

  const handleInstructionsRead = () => {
    setShowInstructions(false);
    setShowUsernameInput(true);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-lg max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h2 className="text-lg font-semibold">
                Telegram KanalÄ± Ekle
              </h2>
              <p className="text-sm text-gray-600">
                {channelType === 'public' ? 'Public (otomatik)' : 'Private (talimatlÄ±)'}
              </p>
            </div>
          </div>
          <button onClick={onClose} className="p-1 hover:bg-gray-100 rounded-lg transition-colors">
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Bot SeÃ§imi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Bot className="inline mr-1" size={16} />
              1. Bot SeÃ§in *
            </label>
            {loadingBots ? (
              <div className="text-sm text-gray-500 py-2">YÃ¼kleniyor...</div>
            ) : bots.length === 0 ? (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                <p className="text-sm text-yellow-800">HenÃ¼z bot eklenmemiÅŸ.</p>
              </div>
            ) : (
              <select
                value={selectedBotId || ''}
                onChange={(e) => handleBotChange(Number(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="">Bir bot seÃ§in...</option>
                {bots.map((bot) => (
                  <option key={bot.id} value={bot.id}>
                    {bot.bot_name} (@{bot.bot_username})
                  </option>
                ))}
              </select>
            )}
            {selectedBot && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-xs text-blue-800">SeÃ§ili: {selectedBot.bot_name} (@{selectedBot.bot_username})</p>
              </div>
            )}
          </div>

          {/* Kanal Tipi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              2. Kanal Tipi *
            </label>
            <div className="flex space-x-4">
              <label className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="radio"
                  value="public"
                  checked={channelType === 'public'}
                  onChange={(e) => setChannelType(e.target.value as 'public')}
                  className="rounded border-gray-300"
                />
                <span>Public (@username)</span>
              </label>
              <label className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="radio"
                  value="private"
                  checked={channelType === 'private'}
                  onChange={(e) => setChannelType(e.target.value as 'private')}
                  className="rounded border-gray-300"
                />
                <span>Private (Davet Linki)</span>
              </label>
            </div>
          </div>

          {/* Kanal Bilgisi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Hash className="inline mr-1" size={16} />
              3. Kanal Bilgisi *
            </label>
            <input
              type="text"
              value={channelIdentifier}
              onChange={(e) => setChannelIdentifier(e.target.value)}
              placeholder={getPlaceholder()}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
              disabled={!selectedBotId}
            />
            <p className="text-xs text-gray-500 mt-2">{getPlaceholder()}</p>
          </div>

          {/* Marka */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              4. Marka SeÃ§in *
            </label>
            <select
              value={selectedBrandId}
              onChange={(e) => setSelectedBrandId(Number(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            >
              <option value={0}>Bir marka seÃ§in...</option>
              {brands.map((brand) => (
                <option key={brand.id} value={brand.id}>
                  {brand.name}
                </option>
              ))}
            </select>
          </div>

          {/* PRIVATE TALÄ°MATLARI */}
          {showInstructions && channelType === 'private' && (
            <div className="p-4 bg-yellow-50 border border-yellow-300 rounded-lg space-y-3">
              <h4 className="text-sm font-semibold text-yellow-800 flex items-center gap-2">
                <AlertCircle size={18} />
                Private Kanal TalimatlarÄ±
              </h4>
              <div className="bg-white p-3 rounded-lg space-y-2 text-sm">
                <p><strong>Davet Linki:</strong> {channelIdentifier}</p>
                <p><strong>Bot:</strong> @{selectedBot?.bot_username}</p>
              </div>
              <div className="bg-blue-50 p-3 rounded-lg space-y-1 text-xs">
                <h5 className="font-medium text-blue-800">AdÄ±mlar:</h5>
                <p>1. YukarÄ±daki davet linkini Telegram'da aÃ§Ä±n ve kanala katÄ±lÄ±n.</p>
                <p>2. Kanala girdikten sonra kanal adÄ±na tÄ±klayÄ±n â†’ 'YÃ¶neticiler'.</p>
                <p>3. 'YÃ¶netici Ekle' â†’ @{selectedBot?.bot_username} botunu ekleyin.</p>
                <p>4. Bot'a 'Mesaj GÃ¶nder', 'DÃ¼zenle', 'Sil' yetkilerini verin.</p>
                <p>5. Kanal username'ini (@kanaladim) girin ve 'DoÄŸrula' butonuna tÄ±klayÄ±n.</p>
              </div>
              <button
                onClick={handleInstructionsRead}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg transition-colors"
              >
                TalimatlarÄ± Okudum - Username Gir
              </button>
            </div>
          )}

          {/* USERNAME INPUT (Private doÄŸrulama) */}
          {showUsernameInput && channelType === 'private' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Kanal Username'i (@kanaladim)
              </label>
              <input
                type="text"
                value={channelUsername}
                onChange={(e) => setChannelUsername(e.target.value)}
                placeholder="@kanaladim"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              />
              <button
                onClick={handleVerifyWithUsername}
                disabled={verifying || !channelUsername.trim()}
                className="mt-2 w-full px-4 py-2 bg-green-600 text-white rounded-lg disabled:opacity-50"
              >
                {verifying ? 'DoÄŸrulanÄ±yor...' : 'DoÄŸrula ve Ekle'}
              </button>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex gap-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg"
              disabled={verifying}
            >
              Ä°ptal
            </button>
            <button
              type="submit"
              disabled={verifying || !selectedBotId || !channelIdentifier.trim() || !selectedBrandId || bots.length === 0}
              className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg disabled:opacity-50"
            >
              {verifying ? (
                <span className="flex items-center gap-2">
                  <RefreshCw className="animate-spin h-4 w-4" />
                  Ä°ÅŸlem YapÄ±lÄ±yor...
                </span>
              ) : (
                channelType === 'public' ? 'ðŸ¤– Otomatik Ekle' : 'Talimat GÃ¶ster'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DynamicChannelModal;
