import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle, Bot, Hash } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { telegramBotsAPI, TelegramBot } from '../../api/telegramBots';
import toast from 'react-hot-toast';

interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [brands, setBrands] = useState<any[]>([]);
  const [selectedBotId, setSelectedBotId] = useState<number | null>(null);
  const [channelIdentifier, setChannelIdentifier] = useState('');
  const [selectedBrandId, setSelectedBrandId] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [loadingBots, setLoadingBots] = useState(false);

  useEffect(() => {
    if (isOpen && platform) {
      loadBots();
      loadBrands();
    }
  }, [isOpen, platform]);

  const loadBots = async () => {
    try {
      setLoadingBots(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots.filter(bot => bot.is_active));
    } catch (error) {
      console.error('Bot listesi yüklenirken hata:', error);
      toast.error('Bot listesi yüklenemedi');
    } finally {
      setLoadingBots(false);
    }
  };

  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Error loading brands:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedBotId) {
      toast.error('Lütfen bir bot seçin');
      return;
    }

    if (!channelIdentifier.trim()) {
      toast.error('Lütfen kanal username veya link girin');
      return;
    }

    if (!selectedBrandId) {
      toast.error('Lütfen bir marka seçin');
      return;
    }

    setLoading(true);
    try {
      await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId,
        channel_identifier: channelIdentifier.trim(),
        brand_id: selectedBrandId
      });

      toast.success('Kanal başarıyla eklendi!');
      onSuccess();
      onClose();

      // Form reset
      setSelectedBotId(null);
      setChannelIdentifier('');
      setSelectedBrandId(0);
    } catch (error: any) {
      console.error('Kanal eklenirken hata:', error);
      const errorMessage = error.response?.data?.detail || 'Kanal eklenemedi';
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  const selectedBot = bots.find(b => b.id === selectedBotId);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-lg max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h2 className="text-lg font-semibold">
                Telegram Kanalı Ekle
              </h2>
              <p className="text-sm text-gray-600">
                Bot seçerek kanal ekleyin
              </p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Step 1: Bot Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Bot className="inline mr-1" size={16} />
              1. Bot Seçin *
            </label>

            {loadingBots ? (
              <div className="text-sm text-gray-500 py-2">Botlar yükleniyor...</div>
            ) : bots.length === 0 ? (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                <p className="text-sm text-yellow-800">
                  ⚠️ Henüz bot eklenmemiş. Önce admin panelden bot ekleyin.
                </p>
              </div>
            ) : (
              <select
                value={selectedBotId || ''}
                onChange={(e) => setSelectedBotId(Number(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              >
                <option value="">Bir bot seçin...</option>
                {bots.map((bot) => (
                  <option key={bot.id} value={bot.id}>
                    {bot.bot_name} (@{bot.bot_username})
                    {bot.channel_count ? ` - ${bot.channel_count} kanal` : ''}
                  </option>
                ))}
              </select>
            )}

            {selectedBot && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-xs text-blue-800">
                  ✓ Seçili bot: <strong>{selectedBot.bot_name}</strong> (@{selectedBot.bot_username})
                </p>
              </div>
            )}
          </div>

          {/* Step 2: Kanal Username/Link */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Hash className="inline mr-1" size={16} />
              2. Kanal Username veya Link *
            </label>
            <input
              type="text"
              value={channelIdentifier}
              onChange={(e) => setChannelIdentifier(e.target.value)}
              placeholder="@kanaladim veya https://t.me/kanaladim"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            />
            <p className="text-xs text-gray-500 mt-2">
              Kanal username'ini (@kanaladim) veya davet linkini girin
            </p>

            {selectedBotId && (
              <div className="mt-3 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <p className="text-xs text-yellow-800 font-medium mb-1">
                  ⚠️ Önemli Adımlar:
                </p>
                <ol className="text-xs text-yellow-800 space-y-1 list-decimal list-inside">
                  <li>Telegram'da kanalınıza gidin</li>
                  <li>Kanal ayarlarından "Yöneticiler" bölümüne gidin</li>
                  <li><strong>@{selectedBot?.bot_username}</strong> botunu yönetici olarak ekleyin</li>
                  <li>Bot'a <strong>mesaj gönderme</strong> yetkisi verin</li>
                  <li>Bu formu doldurun ve ekleyin</li>
                </ol>
              </div>
            )}
          </div>

          {/* Step 3: Marka Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              3. Marka Seçin *
            </label>
            <select
              value={selectedBrandId}
              onChange={(e) => setSelectedBrandId(Number(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            >
              <option value={0}>Bir marka seçin...</option>
              {brands.map((brand) => (
                <option key={brand.id} value={brand.id}>
                  {brand.name}
                </option>
              ))}
            </select>
          </div>

          {/* Form Actions */}
          <div className="flex gap-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              disabled={loading}
            >
              İptal
            </button>
            <button
              type="submit"
              disabled={loading || !selectedBotId || !channelIdentifier.trim() || !selectedBrandId || bots.length === 0}
              className="flex-1 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? 'Ekleniyor...' : 'Kanal Ekle'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DynamicChannelModal;
