import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle, Bot, Hash, RefreshCw } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { telegramBotsAPI, TelegramBot } from '../../api/telegramBots';
import toast from 'react-hot-toast';

interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [brands, setBrands] = useState<any[]>([]);
  const [selectedBotId, setSelectedBotId] = useState<number | null>(null);
  const [inviteLink, setInviteLink] = useState('');
  const [selectedBrandId, setSelectedBrandId] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [loadingBots, setLoadingBots] = useState(false);
  const [joining, setJoining] = useState(false);
  const [selectedBot, setSelectedBot] = useState<TelegramBot | null>(null);

  useEffect(() => {
    if (isOpen && platform) {
      loadBots();
      loadBrands();
    }
  }, [isOpen, platform]);

  const loadBots = async () => {
    try {
      setLoadingBots(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots.filter(bot => bot.is_active));
    } catch (error) {
      console.error('Bot listesi yüklenirken hata:', error);
      toast.error('Bot listesi yüklenemedi');
    } finally {
      setLoadingBots(false);
    }
  };

  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Error loading brands:', error);
    }
  };

  // Bot değiştiğinde
  const handleBotChange = (botId: number) => {
    setSelectedBotId(botId);
    const bot = bots.find(b => b.id === botId);
    setSelectedBot(bot || null);
  };

  // OTOMATİK KANAL EKLEME - Davet linki ile
  const handleJoinAndAddChannel = async () => {
    if (!selectedBotId) {
      toast.error('Lütfen bir bot seçin');
      return;
    }

    if (!inviteLink.trim()) {
      toast.error('Lütfen davet bağlantısı girin');
      return;
    }

    if (!selectedBrandId) {
      toast.error('Lütfen bir marka seçin');
      return;
    }

    setJoining(true);
    const loadingToast = toast.loading('🤖 Bot kanala katılıyor...');

    try {
      // Backend'e davet linkini gönder - Bot otomatik katılacak
      const response = await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId,
        channel_identifier: inviteLink.trim(),
        brand_id: selectedBrandId
      });

      toast.dismiss(loadingToast);
      toast.success('✅ Bot kanala katıldı ve kanal eklendi!', { duration: 4000 });

      onSuccess();
      onClose();

      // Form reset
      setSelectedBotId(null);
      setInviteLink('');
      setSelectedBrandId(0);
      setSelectedBot(null);

    } catch (error: any) {
      toast.dismiss(loadingToast);
      console.error('Kanal ekleme hatası:', error);

      const errorMessage = error.response?.data?.detail || 'İşlem başarısız';

      if (errorMessage.includes('davet') || errorMessage.includes('invite')) {
        toast.error('❌ Davet bağlantısı geçersiz. Lütfen geçerli bir davet linki girin.', { duration: 5000 });
      } else if (errorMessage.includes('üye') || errorMessage.includes('member')) {
        toast.error('❌ Bot kanala katılamadı. Lütfen davet linkinin geçerli ve aktif olduğundan emin olun.', { duration: 5000 });
      } else {
        toast.error(`❌ ${errorMessage}`, { duration: 5000 });
      }
    } finally {
      setJoining(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await handleJoinAndAddChannel();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-lg max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h2 className="text-lg font-semibold">
                Telegram Kanalı Ekle
              </h2>
              <p className="text-sm text-gray-600">
                Bot seçerek kanal ekleyin
              </p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Step 1: Bot Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Bot className="inline mr-1" size={16} />
              1. Bot Seçin *
            </label>

            {loadingBots ? (
              <div className="text-sm text-gray-500 py-2">Botlar yükleniyor...</div>
            ) : bots.length === 0 ? (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                <p className="text-sm text-yellow-800">
                  ⚠️ Henüz bot eklenmemiş. Önce admin panelden bot ekleyin.
                </p>
              </div>
            ) : (
              <select
                value={selectedBotId || ''}
                onChange={(e) => handleBotChange(Number(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              >
                <option value="">Bir bot seçin...</option>
                {bots.map((bot) => (
                  <option key={bot.id} value={bot.id}>
                    {bot.bot_name} (@{bot.bot_username})
                    {bot.channel_count ? ` - ${bot.channel_count} kanal` : ''}
                  </option>
                ))}
              </select>
            )}

            {selectedBot && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-xs text-blue-800">
                  ✓ Seçili bot: <strong>{selectedBot.bot_name}</strong> (@{selectedBot.bot_username})
                </p>
              </div>
            )}
          </div>

          {/* Step 2: Kanal Username/Link */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Hash className="inline mr-1" size={16} />
              2. Kanal Davet Bağlantısı *
            </label>
            <input
              type="text"
              value={inviteLink}
              onChange={(e) => setInviteLink(e.target.value)}
              placeholder="https://t.me/+AbCdEfGhIjK..."
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            />
            <p className="text-xs text-gray-500 mt-2">
              Kanal davet linkini girin. Bot otomatik olarak kanala katılacak.
            </p>

            {selectedBotId && (
              <div className="mt-3 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 border-2 border-blue-300 rounded-xl">
                <h4 className="text-sm font-semibold text-blue-900 mb-3 flex items-center gap-2">
                  <AlertCircle size={18} />
                  📋 Davet Linki Nasıl Alınır?
                </h4>
                <div className="space-y-2">
                  <div className="bg-white p-2 rounded-lg text-xs text-gray-700">
                    <strong>1.</strong> Telegram'da kanalınızı açın
                  </div>
                  <div className="bg-white p-2 rounded-lg text-xs text-gray-700">
                    <strong>2.</strong> Kanal adına tıklayın → <strong>Davet Bağlantıları</strong>
                  </div>
                  <div className="bg-white p-2 rounded-lg text-xs text-gray-700">
                    <strong>3.</strong> <strong>Yeni Bağlantı Oluştur</strong> → Linki kopyalayın
                  </div>
                  <div className="bg-white p-2 rounded-lg text-xs text-gray-700">
                    <strong>4.</strong> Linki buraya yapıştırın
                  </div>
                </div>
                <div className="mt-3 p-2 bg-green-100 border border-green-300 rounded-lg">
                  <p className="text-xs text-green-800 font-medium">
                    ✅ <strong>@{selectedBot?.bot_username}</strong> botu otomatik olarak kanala katılacak ve sisteme eklenecek!
                  </p>
                </div>
              </div>
            )}
          </div>

          {/* Step 3: Marka Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              3. Marka Seçin *
            </label>
            <select
              value={selectedBrandId}
              onChange={(e) => setSelectedBrandId(Number(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            >
              <option value={0}>Bir marka seçin...</option>
              {brands.map((brand) => (
                <option key={brand.id} value={brand.id}>
                  {brand.name}
                </option>
              ))}
            </select>
          </div>

          {/* Ek Talimatlar */}
          {showInstructions && selectedBot && channelIdentifier.trim() && (
            <div className="p-4 bg-gradient-to-r from-orange-50 to-red-50 border-2 border-orange-300 rounded-xl">
              <h4 className="font-semibold text-orange-900 mb-3 flex items-center gap-2">
                <AlertCircle size={20} />
                🚀 Hızlı Kurulum Adımları
              </h4>
              <div className="space-y-3">
                <div className="bg-white p-3 rounded-lg shadow-sm">
                  <p className="text-sm font-medium text-gray-900 mb-2">
                    1️⃣ Telegram'da kanalınıza gidin
                  </p>
                  <p className="text-xs text-gray-600 ml-5">
                    {channelIdentifier.startsWith('@') ? channelIdentifier : `@${channelIdentifier}`} kanalınızı açın
                  </p>
                </div>

                <div className="bg-white p-3 rounded-lg shadow-sm">
                  <p className="text-sm font-medium text-gray-900 mb-2">
                    2️⃣ Botu yönetici olarak ekleyin
                  </p>
                  <div className="text-xs text-gray-600 ml-5 space-y-1">
                    <p>• Kanal ayarları → Yöneticiler → Yönetici Ekle</p>
                    <p className="font-mono bg-blue-50 p-1 rounded">
                      @{selectedBot.bot_username}
                    </p>
                    <p>• <strong>"Mesaj Gönderme"</strong> yetkisini verin</p>
                  </div>
                </div>

                <div className="bg-white p-3 rounded-lg shadow-sm">
                  <p className="text-sm font-medium text-gray-900 mb-2">
                    3️⃣ Botu ekledikten sonra doğrula
                  </p>
                  <p className="text-xs text-gray-600 ml-5">
                    Aşağıdaki "Doğrula ve Ekle" butonuna tıklayın
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex gap-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              disabled={verifying}
            >
              İptal
            </button>
            <button
              type="submit"
              disabled={verifying || !selectedBotId || !channelIdentifier.trim() || !selectedBrandId || bots.length === 0}
              className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white rounded-lg transition-all shadow-md disabled:opacity-50 disabled:cursor-not-allowed font-medium"
            >
              {verifying ? (
                <span className="flex items-center justify-center gap-2">
                  <RefreshCw className="animate-spin" size={16} />
                  Doğrulanıyor...
                </span>
              ) : showInstructions ? (
                '✓ Doğrula ve Ekle'
              ) : (
                'Kanal Ekle'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DynamicChannelModal;
