import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle, Bot, Hash, RefreshCw } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { telegramBotsAPI, TelegramBot } from '../../api/telegramBots';
import toast from 'react-hot-toast';

interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [brands, setBrands] = useState<any[]>([]);
  const [selectedBotId, setSelectedBotId] = useState<number | null>(null);
  const [channelUsername, setChannelUsername] = useState('');
  const [selectedBrandId, setSelectedBrandId] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [loadingBots, setLoadingBots] = useState(false);
  const [selectedBot, setSelectedBot] = useState<TelegramBot | null>(null);
  const [showInstructions, setShowInstructions] = useState(false);
  const [instructions, setInstructions] = useState<string[]>([]);

  useEffect(() => {
    if (isOpen && platform) {
      loadBots();
      loadBrands();
      setChannelUsername('');
      setShowInstructions(false);
      setInstructions([]);
    }
  }, [isOpen, platform]);

  const loadBots = async () => {
    try {
      setLoadingBots(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots.filter(bot => bot.is_active));
    } catch (error) {
      toast.error('Botlar yüklenemedi');
    } finally {
      setLoadingBots(false);
    }
  };

  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Brands error:', error);
    }
  };

  const handleBotChange = (botId: number) => {
    setSelectedBotId(botId);
    const bot = bots.find(b => b.id === botId);
    setSelectedBot(bot || null);
  };

  const handleAddChannel = async () => {
    if (!selectedBotId || !channelUsername.trim() || !selectedBrandId) {
      toast.error('Tüm alanlar gerekli');
      return;
    }

    setLoading(true);
    const loadingToast = toast.loading('Kanal ekleniyor...');

    try {
      const response = await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId,
        channel_identifier: channelUsername.trim(),
        brand_id: selectedBrandId
      });

      if (response.success) {
        toast.success('Kanal eklendi!');
        onSuccess();
        onClose();
      } else {
        setInstructions(response.channel_info.instructions || []);
        setShowInstructions(true);
        toast('Talimatları takip edin', { style: { background: '#FBBF24', color: '#000' } });
      }
    } catch (error: any) {
      toast.error(error.response?.data?.detail || 'Hata oluştu');
    } finally {
      setLoading(false);
      toast.dismiss(loadingToast);
    }
  };

  const handleInstructionsConfirm = async () => {
    // Tekrar ekleme isteği gönder
    await handleAddChannel();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-lg">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <h2 className="text-lg font-semibold">Telegram Kanal Ekle</h2>
          </div>
          <button onClick={onClose} className="p-1 hover:bg-gray-100 rounded">
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <form onSubmit={(e) => { e.preventDefault(); handleAddChannel(); }} className="space-y-6">
          {/* Bot Seçimi */}
          <div>
            <label className="block text-sm font-medium mb-2">
              <Bot className="inline mr-1" size={16} />
              Bot Seçin *
            </label>
            {loadingBots ? (
              <div className="text-sm text-gray-500">Yükleniyor...</div>
            ) : bots.length === 0 ? (
              <div className="bg-yellow-50 border rounded p-3">
                <p className="text-sm text-yellow-800">Bot eklenmemiş.</p>
              </div>
            ) : (
              <select
                value={selectedBotId || ''}
                onChange={(e) => handleBotChange(Number(e.target.value))}
                className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="">Seçin...</option>
                {bots.map((bot) => (
                  <option key={bot.id} value={bot.id}>
                    {bot.bot_name} (@{bot.bot_username})
                  </option>
                ))}
              </select>
            )}
          </div>

          {/* Kanal Username */}
          <div>
            <label className="block text-sm font-medium mb-2">
              <Hash className="inline mr-1" size={16} />
              Kanal Username veya Link *
            </label>
            <input
              type="text"
              value={channelUsername}
              onChange={(e) => setChannelUsername(e.target.value)}
              placeholder="@kanaladim veya https://t.me/kanaladim"
              className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            />
            <p className="text-xs text-gray-500 mt-1">Örn: @pofudukbrands veya https://t.me/pofudukbrands</p>
          </div>

          {/* Marka Seçimi */}
          <div>
            <label className="block text-sm font-medium mb-2">Marka Seçin *</label>
            <select
              value={selectedBrandId}
              onChange={(e) => setSelectedBrandId(Number(e.target.value))}
              className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
              required
            >
              <option value={0}>Seçin...</option>
              {brands.map((brand) => (
                <option key={brand.id} value={brand.id}>
                  {brand.name}
                </option>
              ))}
            </select>
          </div>

          {/* Instructions */}
          {showInstructions && (
            <div className="p-4 bg-yellow-50 border border-yellow-300 rounded-lg space-y-3">
              <h4 className="text-sm font-semibold text-yellow-800 flex items-center gap-2">
                <AlertCircle size={18} />
                Bot Ekleme Talimatları
              </h4>
              <div className="space-y-2 text-sm">
                <p><strong>Kanal:</strong> {instructions[0]}</p>
                <p><strong>Bot:</strong> @{selectedBot?.bot_username}</p>
              </div>
              <ul className="text-xs space-y-1">
                {instructions.slice(1).map((instr, idx) => (
                  <li key={idx} className="flex items-start gap-1">
                    <span>•</span> {instr}
                  </li>
                ))}
              </ul>
              <button
                onClick={handleInstructionsConfirm}
                className="w-full px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
              >
                Tamamlandı - Tekrar Dene
              </button>
            </div>
          )}

          {/* Submit Button */}
          {!showInstructions && (
            <button
              type="submit"
              disabled={loading || !selectedBotId || !channelUsername.trim() || !selectedBrandId}
              className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {loading ? (
                <span className="flex items-center justify-center gap-2">
                  <RefreshCw className="animate-spin h-4 w-4" />
                  Kontrol ediliyor...
                </span>
              ) : (
                'Kanal Ekle'
              )}
            </button>
          )}
        </form>
      </div>
    </div>
  );
};

export default DynamicChannelModal;
