import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle, Bot, Hash, RefreshCw, Link, Users } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { telegramBotsAPI, TelegramBot } from '../../api/telegramBots';
import toast from 'react-hot-toast';

interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [brands, setBrands] = useState<any[]>([]);
  const [selectedBotId, setSelectedBotId] = useState<number | null>(null);
  const [channelType, setChannelType] = useState<'public' | 'private'>('public'); // Yeni: Kanal tipi
  const [channelIdentifier, setChannelIdentifier] = useState(''); // Username/link veya invite link
  const [selectedBrandId, setSelectedBrandId] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [loadingBots, setLoadingBots] = useState(false);
  const [verifying, setVerifying] = useState(false);
  const [selectedBot, setSelectedBot] = useState<TelegramBot | null>(null);
  const [adminInstructionsVisible, setAdminInstructionsVisible] = useState(false); // Yeni: Admin talimatları
  const [joinResult, setJoinResult] = useState<any>(null); // Yeni: Join sonucu

  useEffect(() => {
    if (isOpen && platform) {
      loadBots();
      loadBrands();
      setChannelType('public'); // Varsayılan public
      setChannelIdentifier('');
      setAdminInstructionsVisible(false);
      setJoinResult(null);
    }
  }, [isOpen, platform]);

  const loadBots = async () => {
    try {
      setLoadingBots(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots.filter(bot => bot.is_active));
    } catch (error) {
      console.error('Bot listesi yüklenirken hata:', error);
      toast.error('Bot listesi yüklenemedi');
    } finally {
      setLoadingBots(false);
    }
  };

  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Error loading brands:', error);
    }
  };

  // Bot değiştiğinde
  const handleBotChange = (botId: number) => {
    setSelectedBotId(botId);
    const bot = bots.find(b => b.id === botId);
    setSelectedBot(bot || null);
  };

  // Kanal tipi değiştiğinde placeholder güncelle
  const getPlaceholder = () => {
    if (channelType === 'public') {
      return '@kanaladi veya https://t.me/kanaladi';
    } else {
      return 'https://t.me/+AbCdEfGhIjK... (Davet Linki)';
    }
  };

  // Kanal linkinden username çıkar
  const extractUsername = (link: string) => {
    if (link.startsWith('@')) return link.slice(1);
    if (link.includes('t.me/')) {
      return link.split('t.me/')[1].split('/')[0].split('?')[0].replace('@', '');
    }
    return link;
  };

  // OTOMATİK KANAL EKLEME VE DOĞRULAMA
  const handleJoinAndAddChannel = async () => {
    if (!selectedBotId) {
      toast.error('Lütfen bir bot seçin');
      return;
    }

    if (!channelIdentifier.trim()) {
      toast.error('Lütfen kanal bilgisi girin');
      return;
    }

    if (!selectedBrandId) {
      toast.error('Lütfen bir marka seçin');
      return;
    }

    setVerifying(true);
    const loadingToast = toast.loading(
      channelType === 'public' 
        ? '🤖 Bot public kanala katılıyor...' 
        : '🔗 Bot private kanala katılıyor...'
    );

    try {
      // Backend'e gönder - tip otomatik tespit edilecek
      const response = await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId,
        channel_identifier: channelIdentifier.trim(),
        brand_id: selectedBrandId
      });

      toast.dismiss(loadingToast);

      if (response.success) {
        toast.success('✅ Kanal başarıyla eklendi!');
        onSuccess();
        onClose();
      } else {
        // Bot katıldı ama yönetici değil - Talimat göster
        setJoinResult(response);
        setAdminInstructionsVisible(true);
        toast.success('✅ Bot kanala katıldı! Şimdi yönetici yapın.');

        // Modal'ı kapatmadan talimatları göster
        // Modal açık kalır
      }

    } catch (error: any) {
      toast.dismiss(loadingToast);
      console.error('Kanal ekleme hatası:', error);

      const errorMessage = error.response?.data?.detail || 'İşlem başarısız';

      if (errorMessage.includes('public')) {
        toast.error('❌ Public kanal bulunamadı. @username veya https://t.me/username formatını kullanın.', { duration: 6000 });
      } else if (errorMessage.includes('davet') || errorMessage.includes('private')) {
        toast.error('❌ Private kanal davet linki geçersiz. Yeni bir davet linki oluşturun.', { duration: 6000 });
      } else if (errorMessage.includes('yönetici')) {
        setAdminInstructionsVisible(true);
        toast.error('⚠️ Bot kanala katıldı ancak yönetici değil. Lütfen talimatları takip edin.', { duration: 6000 });
      } else {
        toast.error(`❌ ${errorMessage}`, { duration: 6000 });
      }
    } finally {
      setVerifying(false);
    }
  };

  const handleAdminConfirmed = async () => {
    if (!joinResult || !joinResult.channel_info) return;

    setVerifying(true);
    const loadingToast = toast.loading('🔍 Bot yönetici yetkilerini doğrulanıyor...');

    try {
      // Backend'e tekrar doğrula gönder
      const response = await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId!,
        channel_identifier: channelIdentifier.trim(),
        brand_id: selectedBrandId
      });

      toast.dismiss(loadingToast);

      if (response.success) {
        toast.success('✅ Kanal başarıyla eklendi! Bot yönetici olarak atandı.');
        onSuccess();
        onClose();
      } else {
        toast.error('❌ Doğrulama başarısız. Lütfen talimatları tekrar kontrol edin.');
      }

    } catch (error: any) {
      toast.dismiss(loadingToast);
      toast.error('❌ Doğrulama hatası. Lütfen sayfayı yenileyin ve tekrar deneyin.');
    } finally {
      setVerifying(false);
      setAdminInstructionsVisible(false);
      setJoinResult(null);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await handleJoinAndAddChannel();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-lg max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h2 className="text-lg font-semibold">
                Telegram Kanalı Ekle
              </h2>
              <p className="text-sm text-gray-600">
                {channelType === 'public' ? 'Public kanal (otomatik)' : 'Private kanal (davet linki)'}
              </p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Step 1: Bot Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Bot className="inline mr-1" size={16} />
              1. Bot Seçin *
            </label>

            {loadingBots ? (
              <div className="text-sm text-gray-500 py-2">Botlar yükleniyor...</div>
            ) : bots.length === 0 ? (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                <p className="text-sm text-yellow-800">
                  ⚠️ Henüz bot eklenmemiş. Önce admin panelden bot ekleyin.
                </p>
              </div>
            ) : (
              <select
                value={selectedBotId || ''}
                onChange={(e) => handleBotChange(Number(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              >
                <option value="">Bir bot seçin...</option>
                {bots.map((bot) => (
                  <option key={bot.id} value={bot.id}>
                    {bot.bot_name} (@{bot.bot_username})
                    {bot.channel_count ? ` - ${bot.channel_count} kanal` : ''}
                  </option>
                ))}
              </select>
            )}

            {selectedBot && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-xs text-blue-800">
                  ✓ Seçili bot: <strong>{selectedBot.bot_name}</strong> (@{selectedBot.bot_username})
                </p>
              </div>
            )}
          </div>

          {/* Step 2: Kanal Tipi Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              2. Kanal Tipi Seçin *
            </label>
            <div className="flex space-x-4">
              <label className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="radio"
                  value="public"
                  checked={channelType === 'public'}
                  onChange={(e) => setChannelType(e.target.value as 'public')}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-sm">Public Kanal (@username)</span>
              </label>
              <label className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="radio"
                  value="private"
                  checked={channelType === 'private'}
                  onChange={(e) => setChannelType(e.target.value as 'private')}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-sm">Private Kanal (Davet Linki)</span>
              </label>
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Public: Bot otomatik katılır | Private: Davet linki gerekir
            </p>
          </div>

          {/* Step 3: Kanal Bilgisi Girişi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Hash className="inline mr-1" size={16} />
              {channelType === 'public' ? '3. Kanal Username/Link *' : '3. Davet Linki *'}
            </label>
            <input
              type="text"
              value={channelIdentifier}
              onChange={(e) => setChannelIdentifier(e.target.value)}
              placeholder={getPlaceholder()}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            />
            <p className="text-xs text-gray-500 mt-2">
              {channelType === 'public' 
                ? 'Örn: @pofudukbrands veya https://t.me/pofudukbrands'
                : 'Örn: https://t.me/+AbCdEfGhIjK... (Private için)'
              }
            </p>
          </div>

          {/* Step 4: Marka Seçimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              4. Marka Seçin *
            </label>
            <select
              value={selectedBrandId}
              onChange={(e) => setSelectedBrandId(Number(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            >
              <option value={0}>Bir marka seçin...</option>
              {brands.map((brand) => (
                <option key={brand.id} value={brand.id}>
                  {brand.name}
                </option>
              ))}
            </select>
          </div>

          {/* ADMIN TALİMATLARI (Private için veya bot yönetici değilse) */}
          {adminInstructionsVisible && joinResult && (
            <div className="p-4 bg-yellow-50 border border-yellow-300 rounded-lg space-y-3">
              <h4 className="text-sm font-semibold text-yellow-800 flex items-center gap-2">
                <AlertCircle size={18} />
                ⚠️ Bot Kanala Katıldı - Şimdi Yönetici Yapın
              </h4>
              <div className="bg-white p-3 rounded-lg space-y-2 text-sm">
                <p><strong>Kanal:</strong> {joinResult.channel_info.title}</p>
                <p><strong>Bot:</strong> @{selectedBot?.bot_username}</p>
              </div>
              <div className="bg-blue-50 p-3 rounded-lg space-y-1 text-xs">
                <h5 className="font-medium text-blue-800">Talimatlar:</h5>
                {joinResult.channel_info.instructions?.map((instr: string, idx: number) => (
                  <p key={idx} className="text-blue-700">{instr}</p>
                )) || (
                  <>
                    <p>1. Telegram'da kanala gidin</p>
                    <p>2. Kanal adına tıklayın → 'Yöneticiler'</p>
                    <p>3. 'Yönetici Ekle' → @{selectedBot?.bot_username} aratın</p>
                    <p>4. 'Mesaj Gönder, Düzenle, Sil' yetkilerini verin</p>
                    <p>5. Aşağıdaki butona tıklayın</p>
                  </>
                )}
              </div>
              <button
                onClick={handleAdminConfirmed}
                disabled={verifying}
                className="w-full px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors font-medium"
              >
                {verifying ? (
                  <span className="flex items-center justify-center gap-2">
                    <RefreshCw className="animate-spin h-4 w-4" />
                    Doğrulanıyor...
                  </span>
                ) : (
                  '✅ Botu Yönetici Yaptım - Devam Et'
                )}
              </button>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex gap-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              disabled={verifying}
            >
              İptal
            </button>
            <button
              type="submit"
              disabled={verifying || !selectedBotId || !channelIdentifier.trim() || !selectedBrandId || bots.length === 0}
              className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white rounded-lg transition-all shadow-md disabled:opacity-50 disabled:cursor-not-allowed font-medium"
            >
              {verifying ? (
                <span className="flex items-center justify-center gap-2">
                  <RefreshCw className="animate-spin" size={16} />
                  {channelType === 'public' ? 'Katılıyor...' : 'Davet Ediliyor...'}
                </span>
              ) : (
                <span className="flex items-center justify-center gap-2">
                  {channelType === 'public' ? '🤖 Otomatik Ekle' : '🔗 Davet Linki ile Ekle'}
                </span>
              )}
            </button>
          </div>
        </form>

        {/* Talimat Kartı (Public için otomatik, Private için detaylı) */}
        {selectedBotId && (
          <div className="mt-4 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 border-2 border-blue-300 rounded-xl">
            <h4 className="text-sm font-semibold text-blue-900 mb-3 flex items-center gap-2">
              <AlertCircle size={18} />
              {channelType === 'public' ? '📋 Public Kanal Talimatları' : '📋 Private Kanal Talimatları'}
            </h4>
            <div className="space-y-2 text-xs">
              {channelType === 'public' ? (
                <>
                  <div className="bg-white p-2 rounded-lg">
                    <strong>✅ Otomatik İşlem:</strong> Bot seçili kanala otomatik katılacak.
                  </div>
                  <div className="bg-white p-2 rounded-lg">
                    <strong>⚠️ Manuel Adım:</strong> Bot katıldığında, kanalda yönetici yapın (Mesaj Gönder yetkisi ile).
                  </div>
                  <div className="bg-green-100 p-2 rounded-lg">
                    <p className="text-green-800 font-medium">
                      Seçili bot: <strong>@{selectedBot?.bot_username}</strong> otomatik katılacak!
                    </p>
                  </div>
                </>
              ) : (
                <>
                  <div className="bg-white p-2 rounded-lg">
                    <strong>1. Davet Linki:</strong> Yukarıdaki linki girin.
                  </div>
                  <div className="bg-white p-2 rounded-lg">
                    <strong>2. Bot Katılımı:</strong> Bot davet linki ile otomatik katılacak.
                  </div>
                  <div className="bg-white p-2 rounded-lg">
                    <strong>3. Yönetici Yapma:</strong> Bot katıldığında, kanalda yönetici olarak ekleyin.
                  </div>
                  <div className="bg-green-100 p-2 rounded-lg">
                    <p className="text-green-800 font-medium">
                      ✅ <strong>@{selectedBot?.bot_username}</strong> botu otomatik katılacak ve yönetici yapılacak!
                    </p>
                  </div>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DynamicChannelModal;
