import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { telegramBotsAPI, TelegramBot, TelegramBotCreate } from '../../api/telegramBots';
import { Plus, Trash2, CheckCircle, XCircle, RefreshCw, Eye, EyeOff } from 'lucide-react';
import toast from 'react-hot-toast';

const TelegramBots: React.FC = () => {
  const { user } = useAuth();
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showTokens, setShowTokens] = useState<Record<number, boolean>>({});
  const [formData, setFormData] = useState<TelegramBotCreate>({
    bot_token: ''
  });
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    loadBots();
  }, []);

  const loadBots = async () => {
    try {
      setLoading(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots);
    } catch (error: any) {
      console.error('Bot listesi yüklenirken hata:', error);
      toast.error(error.response?.data?.detail || 'Bot listesi yüklenemedi');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.bot_token || formData.bot_token.trim() === '') {
      toast.error('Lütfen bot token girin');
      return;
    }

    setSubmitting(true);
    try {
      const response = await telegramBotsAPI.createBot(formData);
      toast.success(`Bot başarıyla eklendi! ${response.discovered_channels_count || 0} kanal otomatik keşfedildi ve eklendi.`);
      setShowModal(false);
      setFormData({ bot_token: '' });
      loadBots();
      // Optionally navigate to channels
      // navigate('/admin/telegram/channels');
    } catch (error: any) {
      console.error('Bot eklenirken hata:', error);
      toast.error(String(error.response?.data?.detail || 'Bot eklenemedi'));
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (botId: number) => {
    if (!window.confirm('Bu botu silmek istediğinizden emin misiniz?')) {
      return;
    }

    try {
      await telegramBotsAPI.deleteBot(botId);
      toast.success('Bot başarıyla silindi');
      loadBots();
    } catch (error: any) {
      console.error('Bot silinirken hata:', error);
      toast.error(error.response?.data?.detail || 'Bot silinemedi');
    }
  };

  const handleVerify = async (botId: number) => {
    try {
      const result = await telegramBotsAPI.verifyBot(botId);
      if (result.success) {
        toast.success('Bot token geçerli ✓');
        loadBots();
      } else {
        toast.error(result.message);
      }
    } catch (error: any) {
      console.error('Bot doğrulanırken hata:', error);
      toast.error(error.response?.data?.detail || 'Bot doğrulanamadı');
    }
  };

  const toggleTokenVisibility = (botId: number) => {
    setShowTokens(prev => ({
      ...prev,
      [botId]: !prev[botId]
    }));
  };

  const maskToken = (token: string) => {
    if (token.length <= 8) return '••••••••';
    return `${token.substring(0, 8)}••••••••${token.substring(token.length - 8)}`;
  };

  // Admin kontrolü
  const isAdmin = user?.permissions?.includes('social.manage');

  if (!isAdmin) {
    return (
      <div className="p-8">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-red-700">
          Bu sayfaya erişim yetkiniz yok. Sadece adminler bot yönetimi yapabilir.
        </div>
      </div>
    );
  }

  return (
    <div className="p-8">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Telegram Botları</h1>
          <p className="text-sm text-gray-600 mt-1">
            Telegram bot token'larını yönetin
          </p>
        </div>
        <button
          onClick={() => setShowModal(true)}
          className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          <Plus size={20} />
          Bot Ekle
        </button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="bg-white rounded-lg shadow p-4">
          <div className="text-sm text-gray-600">Toplam Bot</div>
          <div className="text-2xl font-bold text-gray-900">{bots.length}</div>
        </div>
        <div className="bg-white rounded-lg shadow p-4">
          <div className="text-sm text-gray-600">Aktif Bot</div>
          <div className="text-2xl font-bold text-green-600">
            {bots.filter(b => b.is_active).length}
          </div>
        </div>
        <div className="bg-white rounded-lg shadow p-4">
          <div className="text-sm text-gray-600">Toplam Kanal</div>
          <div className="text-2xl font-bold text-blue-600">
            {bots.reduce((sum, b) => sum + (b.channel_count || 0), 0)}
          </div>
        </div>
      </div>

      {/* Bot List */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        {loading ? (
          <div className="p-8 text-center text-gray-500">Yükleniyor...</div>
        ) : bots.length === 0 ? (
          <div className="p-8 text-center text-gray-500">
            Henüz bot eklenmemiş. "Bot Ekle" butonuna tıklayarak başlayın.
          </div>
        ) : (
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Bot Bilgileri
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Token
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Durum
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Kanallar
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  İşlemler
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {bots.map((bot) => (
                <tr key={bot.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4">
                    <div>
                      <div className="font-medium text-gray-900">{bot.bot_name}</div>
                      <div className="text-sm text-gray-500">@{bot.bot_username}</div>
                      {bot.creator_name && (
                        <div className="text-xs text-gray-400 mt-1">
                          Ekleyen: {bot.creator_name}
                        </div>
                      )}
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-2">
                      <code className="text-xs bg-gray-100 px-2 py-1 rounded font-mono">
                        {showTokens[bot.id] ? bot.bot_user_id || 'N/A' : maskToken(bot.bot_user_id || '')}
                      </code>
                      <button
                        onClick={() => toggleTokenVisibility(bot.id)}
                        className="text-gray-400 hover:text-gray-600"
                      >
                        {showTokens[bot.id] ? <EyeOff size={16} /> : <Eye size={16} />}
                      </button>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex flex-col gap-1">
                      <div className="flex items-center gap-2">
                        {bot.is_active ? (
                          <span className="flex items-center gap-1 text-green-600 text-sm">
                            <CheckCircle size={16} />
                            Aktif
                          </span>
                        ) : (
                          <span className="flex items-center gap-1 text-red-600 text-sm">
                            <XCircle size={16} />
                            Pasif
                          </span>
                        )}
                      </div>
                      {bot.is_verified ? (
                        <span className="text-xs text-green-600">✓ Doğrulandı</span>
                      ) : (
                        <span className="text-xs text-yellow-600">⚠ Doğrulanmadı</span>
                      )}
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <span className="text-sm text-gray-700">
                      {bot.channel_count || 0} kanal
                    </span>
                  </td>
                  <td className="px-6 py-4 text-right">
                    <div className="flex items-center justify-end gap-2">
                      <button
                        onClick={() => handleVerify(bot.id)}
                        className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                        title="Bot doğrula"
                      >
                        <RefreshCw size={18} />
                      </button>
                      <button
                        onClick={() => handleDelete(bot.id)}
                        className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                        title="Botu sil"
                        disabled={!!(bot.channel_count && bot.channel_count > 0)}
                      >
                        <Trash2 size={18} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* Add Bot Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-4">Yeni Bot Ekle</h2>
            
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 mb-4">
                <p className="text-sm text-blue-800">
                  ℹ️ Sadece bot token'ını girin. Bot adı ve kullanıcı adı otomatik olarak alınacak!
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Bot Token *
                </label>
                <textarea
                  value={formData.bot_token}
                  onChange={(e) => setFormData({ bot_token: e.target.value })}
                  placeholder="1234567890:ABCdefGHIjklMNOpqrsTUVwxyz"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent font-mono text-sm resize-none"
                  rows={3}
                  required
                />
                <p className="text-xs text-gray-500 mt-2">
                  <a 
                    href="https://t.me/BotFather" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-600 hover:underline"
                  >
                    @BotFather
                  </a>'a "/newbot" yazarak yeni bot oluşturun ve token'ı buraya yapıştırın.
                </p>
              </div>

              <div className="flex gap-3 mt-6">
                <button
                  type="button"
                  onClick={() => {
                    setShowModal(false);
                    setFormData({ bot_token: '' });
                  }}
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                  disabled={submitting}
                >
                  İptal
                </button>
                <button
                  type="submit"
                  className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
                  disabled={submitting}
                >
                  {submitting ? 'Ekleniyor...' : 'Bot Ekle'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TelegramBots;
