import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import toast from 'react-hot-toast';


interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [formData, setFormData] = useState<Record<string, any>>({});
  const [brands, setBrands] = useState<any[]>([]);
  const [addingChannel, setAddingChannel] = useState(false);
  const [discovering, setDiscovering] = useState(false);
  const [discoveredChats, setDiscoveredChats] = useState<any[]>([]);
  const [botInfo, setBotInfo] = useState<any>(null);
  const [selectedChat, setSelectedChat] = useState<any>(null);
  const [selectedChats, setSelectedChats] = useState<Record<string, any>>({});
  const [step, setStep] = useState<'token' | 'channels' | 'manual' | 'brand'>('token');

  useEffect(() => {
    if (isOpen && platform) {
      loadBrands();
      initializeFormData();
    }
  }, [isOpen, platform, editingChannel]); // eslint-disable-line react-hooks/exhaustive-deps


  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Error loading brands:', error);
    }
  };

  const initializeFormData = () => {
    if (editingChannel) {
      setFormData({
        brand_id: editingChannel.brand_id,
        bot_token: editingChannel.bot_token || ''
      });
    } else {
      setFormData({
        brand_id: 0,
        bot_token: ''
      });
    }
    setSelectedChat(null);
    setStep('token');
    setDiscoveredChats([]);
    setBotInfo(null);
  };




  const handleInputChange = (fieldName: string, value: any) => {
    setFormData(prev => ({
      ...prev,
      [fieldName]: value
    }));

    // Bot token deƒüi≈ütiƒüinde otomatik ke≈üfet
    if (fieldName === 'bot_token' && value && value.length > 35) {
      // Kƒ±sa bir gecikme ile ke≈üfet (kullanƒ±cƒ± yazmayƒ± bitirsin)
      setTimeout(() => {
        if (formData.bot_token === value) { // Kullanƒ±cƒ± hala aynƒ± token'ƒ± yazƒ±yor mu?
          discoverChannels(value);
        }
      }, 1000);
    }
  };

  const discoverChannels = async (token: string) => {
    setDiscovering(true);
    try {
      const res = await socialMediaChannelsAPI.discoverTelegramChats(token);
      setDiscoveredChats(res.chats || []);
      setBotInfo(res.bot_info);

      // API'den gelen mesajƒ± kontrol et
      if (res.message) {
        toast(res.message, { 
          icon: '‚ÑπÔ∏è',
          duration: 6000 
        });

        // Talimatlarƒ± g√∂ster
        if (res.instructions && res.instructions.length > 0) {
          toast(res.instructions.join(' ‚Ä¢ '), {
            icon: 'üí°',
            duration: 8000
          });
        }
      }

      if (!res.chats || res.chats.length === 0) {
        // Otomatik ke≈üif ba≈üarƒ±sƒ±z, manuel giri≈ü se√ßeneƒüi sun
        setStep('manual');
      } else {
        toast.success(`${res.chats.length} kanal bulundu`);
        setStep('channels');
      }
    } catch (e: any) {
      const status = e?.response?.status;
      if (status === 401) {
        toast.error('Oturum s√ºresi doldu. L√ºtfen tekrar giri≈ü yapƒ±n.');
      } else {
        const msg = e?.response?.data?.detail || 'Bot token ge√ßersiz';
        toast.error(`‚ùå ${msg}`);
      }
      setStep('token');
    } finally {
      setDiscovering(false);
    }
  };

  const toggleSelectChat = (chat: any) => {
    setSelectedChats(prev => {
      const next = { ...prev } as Record<string, any>;
      if (next[chat.chat_id]) {
        delete next[chat.chat_id];
      } else {
        next[chat.chat_id] = chat;
      }
      return next;
    });
  };

  const proceedToBrand = () => {
    const first = Object.values(selectedChats)[0];
    if (!first) {
      toast.error('En az bir kanal se√ßin');
      return;
    }
    setSelectedChat(first);
    setStep('brand');
  };

  const handleAddSelectedChannels = async () => {
    if (!formData.bot_token || !formData.brand_id) {
      toast.error('Bot token ve marka se√ßimi gerekli');
      return;
    }
    const chats = Object.values(selectedChats);
    if (!chats.length) {
      toast.error('En az bir kanal se√ßin');
      return;
    }
    setAddingChannel(true);
    let successCount = 0;
    let skippedCount = 0;
    for (const chat of chats) {
      // √ñn kontrol: private sohbetler kanal olarak eklenemez
      if (chat.type === 'private') {
        skippedCount += 1;
        toast('‚ÑπÔ∏è √ñzel sohbetler eklenemez: ' + (chat.username || chat.chat_id), { icon: '‚ÑπÔ∏è' });
        continue;
      }
      try {
        const username = chat.username ? `@${chat.username}` : null;
        const chatId = chat.chat_id ? String(chat.chat_id) : undefined;
        // Uyarƒ±: grup/kanal chat_id genelde -100 ile ba≈ülar
        if (chatId && !String(chatId).startsWith('-')) {
          toast('‚ÑπÔ∏è Bu sohbet kimliƒüi kanal/grup olmayabilir: ' + chatId, { icon: '‚ÑπÔ∏è' });
        }
        await socialMediaChannelsAPI.addChannelByUsername(
          formData.bot_token,
          username,
          formData.brand_id,
          chatId
        );
        successCount += 1;
        toast.success(`${chat.title || chat.username || chat.chat_id}: eklendi`);
      } catch (e: any) {
        const detail = e?.response?.data?.detail || '';
        if (typeof detail === 'string' && detail.includes('zaten eklenmi≈ü')) {
          // Mevcutta varsa ba≈üarƒ± sayalƒ±m ve uyarƒ± verelim
          successCount += 1;
          skippedCount += 1;
          toast(`‚ÑπÔ∏è ${chat.title || chat.username || chat.chat_id}: zaten ekli`, { icon: '‚ÑπÔ∏è' });
        } else if (typeof detail === 'string' && (detail.includes('y√∂netici') || detail.toLowerCase().includes('admin'))) {
          toast.error(`‚ùå ${chat.title || chat.username || chat.chat_id}: Bot bu kanalda y√∂netici deƒüil`);
        } else if (typeof detail === 'string' && (detail.toLowerCase().includes('bulunamadƒ±') || detail.toLowerCase().includes('not found'))) {
          toast.error(`‚ùå ${chat.title || chat.username || chat.chat_id}: Kanal bulunamadƒ±`);
        } else {
          const msg = detail || 'Kanal eklenemedi';
          toast.error(`‚ùå ${chat.title || chat.username || chat.chat_id}: ${msg}`);
        }
      }
    }
    setAddingChannel(false);
    if (successCount > 0) {
      const addedOnly = successCount - skippedCount;
      if (addedOnly > 0) {
        toast.success(`${addedOnly} yeni kanal eklendi`);
      }
      if (skippedCount > 0) {
        toast(`‚ÑπÔ∏è ${skippedCount} kanal zaten sistemdeydi`, { icon: '‚ÑπÔ∏è' });
      }
      onSuccess();
      setSelectedChats({});
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h2 className="text-lg font-semibold">
                {editingChannel ? 'Kanal D√ºzenle' : 'Telegram Kanalƒ± Ekle'}
              </h2>
              <p className="text-sm text-gray-600">
                Telegram bot bilgilerini girin
              </p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <div className="space-y-6">
          {/* Step 1: Bot Token */}
          {step === 'token' && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Bot Token *
                </label>
                <input
                  type="password"
                  value={formData.bot_token || ''}
                  onChange={(e) => handleInputChange('bot_token', e.target.value)}
                  placeholder="123456789:ABCDEFGHIJKLMNOPQRSTUVWXYZ123456789"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                />
                <p className="text-xs text-gray-500 mt-1">
                  BotFather'dan aldƒ±ƒüƒ±nƒ±z tokeni girin. Token girildikten sonra otomatik olarak kanallar ke≈üfedilecek.
                </p>
              </div>

              {discovering && (
                <div className="text-center py-4">
                  <div className="inline-flex items-center gap-2 text-blue-600">
                    <div className="w-4 h-4 border-2 border-blue-600 border-t-transparent rounded-full animate-spin" />
                    Kanallar ke≈üfediliyor...
                  </div>
                </div>
              )}
            </>
          )}

          {/* Step 2: Channel Selection */}
          {step === 'channels' && (
            <>
              {botInfo && (
                <div className="bg-green-50 border border-green-200 rounded-lg p-3 mb-4">
                  <p className="text-sm text-green-800">
                    <strong>Bot:</strong> {botInfo.first_name} (@{botInfo.username})
                  </p>
                </div>
              )}

              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-3">
                  Botun Eri≈üebildiƒüi Kanallar ({discoveredChats.length})
                </h3>
                <div className="max-h-60 overflow-y-auto space-y-2">
                  {discoveredChats.map((chat) => {
                    const checked = Boolean(selectedChats[chat.chat_id]);
                    return (
                      <label
                        key={chat.chat_id}
                        className={`flex items-center justify-between p-3 border rounded-lg cursor-pointer transition-colors ${
                          checked ? 'border-blue-500 bg-blue-50' : 'border-gray-200 hover:border-gray-300'
                        }`}
                      >
                        <div className="flex items-center gap-3">
                          <input
                            type="checkbox"
                            className="h-4 w-4"
                            checked={checked}
                            onChange={() => toggleSelectChat(chat)}
                          />
                          <div>
                            <h4 className="font-medium text-gray-900">
                              {chat.title || (chat.username ? `@${chat.username}` : `Kanal ${chat.chat_id}`)}
                            </h4>
                            <p className="text-sm text-gray-500">
                              {chat.type === 'channel' ? 'Kanal' : chat.type === 'supergroup' ? 'S√ºper Grup' : 'Kanal'}
                              {chat.member_count && ` ‚Ä¢ ${chat.member_count} √ºye`}
                              {chat.is_admin && (
                                <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                                  Admin
                                </span>
                              )}
                            </p>
                          </div>
                        </div>
                      </label>
                    );
                  })}
                </div>
                <div className="mt-3 flex justify-end">
                  <button
                    onClick={proceedToBrand}
                    className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg"
                  >
                    Se√ßilenlerle Devam Et
                  </button>
                </div>
              </div>
            </>
          )}

          {/* Step 3: Brand Selection */}
          {step === 'brand' && selectedChat && (
            <>
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                <p className="text-sm text-blue-800">
                  <strong>Se√ßilen Kanal:</strong> {selectedChat.title || (selectedChat.username ? `@${selectedChat.username}` : `Kanal ${selectedChat.chat_id}`)}
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Marka *
                </label>
                <select
                  value={formData.brand_id || 0}
                  onChange={(e) => handleInputChange('brand_id', parseInt(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                >
                  <option value={0}>Marka se√ßin</option>
                  {brands.map(brand => (
                    <option key={brand.id} value={brand.id}>{brand.name}</option>
                  ))}
                </select>
              </div>

              <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                <div className="flex items-start gap-2">
                  <AlertCircle className="h-4 w-4 text-green-600 mt-0.5 flex-shrink-0" />
                  <div className="text-sm text-green-800">
                    <p className="font-medium">Hazƒ±r!</p>
                    <p className="mt-1">
                      Kanal adƒ±, √ºye sayƒ±sƒ± ve diƒüer bilgiler otomatik olarak √ßekilecek.
                    </p>
                  </div>
                </div>
              </div>
            </>
          )}

          {/* Butonlar */}
          <div className="flex gap-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
            >
              ƒ∞ptal
            </button>

            {step === 'token' && (
              <button
                onClick={() => discoverChannels(formData.bot_token)}
                disabled={!formData.bot_token || discovering}
                className="flex-1 flex items-center justify-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {discovering ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    Ke≈üfediliyor...
                  </>
                ) : (
                  <>
                    <Send size={16} />
                    Kanallarƒ± Ke≈üfet
                  </>
                )}
              </button>
            )}

            {step === 'channels' && (
              <button
                onClick={() => setStep('token')}
                className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              >
                Geri
              </button>
            )}

            {step === 'brand' && (
              <>
                <button
                  onClick={() => setStep('channels')}
                  className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  Geri
                </button>
                <button
                  onClick={handleAddSelectedChannels}
                  disabled={addingChannel || !formData.brand_id}
                  className="flex-1 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {addingChannel ? 'Ekleniyor...' : 'Se√ßilenleri Ekle'}
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DynamicChannelModal;
