import React, { useState, useEffect } from 'react';
import { X, Send, AlertCircle, Bot, Hash, RefreshCw } from 'lucide-react';
import { socialMediaChannelsAPI } from '../../api/socialMediaChannels';
import { brandsAPI } from '../../api/brands';
import { telegramBotsAPI, TelegramBot } from '../../api/telegramBots';
import toast from 'react-hot-toast';

interface ChannelModalProps {
  isOpen: boolean;
  onClose: () => void;
  platform: 'telegram' | null;
  onSuccess: () => void;
  editingChannel?: any;
}

const DynamicChannelModal: React.FC<ChannelModalProps> = ({
  isOpen,
  onClose,
  platform,
  onSuccess,
  editingChannel
}) => {
  const [bots, setBots] = useState<TelegramBot[]>([]);
  const [brands, setBrands] = useState<any[]>([]);
  const [selectedBotId, setSelectedBotId] = useState<number | null>(null);
  const [channelIdentifier, setChannelIdentifier] = useState('');
  const [selectedBrandId, setSelectedBrandId] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [loadingBots, setLoadingBots] = useState(false);
  const [verifying, setVerifying] = useState(false);
  const [showInstructions, setShowInstructions] = useState(false);
  const [selectedBot, setSelectedBot] = useState<TelegramBot | null>(null);

  useEffect(() => {
    if (isOpen && platform) {
      loadBots();
      loadBrands();
    }
  }, [isOpen, platform]);

  const loadBots = async () => {
    try {
      setLoadingBots(true);
      const response = await telegramBotsAPI.getBots();
      setBots(response.bots.filter(bot => bot.is_active));
    } catch (error) {
      console.error('Bot listesi y√ºklenirken hata:', error);
      toast.error('Bot listesi y√ºklenemedi');
    } finally {
      setLoadingBots(false);
    }
  };

  const loadBrands = async () => {
    try {
      const response = await brandsAPI.getBrands(1, 1000);
      setBrands(response.brands);
    } catch (error) {
      console.error('Error loading brands:', error);
    }
  };

  // Bot deƒüi≈ütiƒüinde talimatlarƒ± g√∂ster
  const handleBotChange = (botId: number) => {
    setSelectedBotId(botId);
    const bot = bots.find(b => b.id === botId);
    setSelectedBot(bot || null);
    if (botId && channelIdentifier.trim()) {
      setShowInstructions(true);
    }
  };

  // Kanal identifier deƒüi≈ütiƒüinde
  const handleChannelChange = (value: string) => {
    setChannelIdentifier(value);
    if (selectedBotId && value.trim()) {
      setShowInstructions(true);
    }
  };

  // Bot doƒürulama ve kanal ekleme
  const handleVerifyAndAdd = async () => {
    if (!selectedBotId) {
      toast.error('L√ºtfen bir bot se√ßin');
      return;
    }

    if (!channelIdentifier.trim()) {
      toast.error('L√ºtfen kanal username veya link girin');
      return;
    }

    if (!selectedBrandId) {
      toast.error('L√ºtfen bir marka se√ßin');
      return;
    }

    setVerifying(true);
    try {
      // Botu kanala ekle ve doƒürula
      await socialMediaChannelsAPI.addChannelByBot({
        telegram_bot_id: selectedBotId,
        channel_identifier: channelIdentifier.trim(),
        brand_id: selectedBrandId
      });

      toast.success('‚úÖ Bot doƒürulandƒ± ve kanal eklendi!');
      onSuccess();
      onClose();

      // Form reset
      setSelectedBotId(null);
      setChannelIdentifier('');
      setSelectedBrandId(0);
      setShowInstructions(false);
      setSelectedBot(null);
    } catch (error: any) {
      console.error('Kanal eklenirken hata:', error);
      const errorMessage = error.response?.data?.detail || 'Kanal eklenemedi';

      // Eƒüer bot y√∂netici deƒüilse, talimatlarƒ± g√∂ster
      if (errorMessage.includes('y√∂netici deƒüil') || errorMessage.includes('admin')) {
        toast.error('‚ö†Ô∏è Bot kanalda y√∂netici deƒüil. L√ºtfen √∂nce botu kanala y√∂netici olarak ekleyin.');
        setShowInstructions(true);
      } else {
        toast.error(errorMessage);
      }
    } finally {
      setVerifying(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await handleVerifyAndAdd();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-lg max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-blue-100">
              <Send className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <h2 className="text-lg font-semibold">
                Telegram Kanalƒ± Ekle
              </h2>
              <p className="text-sm text-gray-600">
                Bot se√ßerek kanal ekleyin
              </p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X size={20} className="text-gray-500" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Step 1: Bot Se√ßimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Bot className="inline mr-1" size={16} />
              1. Bot Se√ßin *
            </label>

            {loadingBots ? (
              <div className="text-sm text-gray-500 py-2">Botlar y√ºkleniyor...</div>
            ) : bots.length === 0 ? (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                <p className="text-sm text-yellow-800">
                  ‚ö†Ô∏è Hen√ºz bot eklenmemi≈ü. √ñnce admin panelden bot ekleyin.
                </p>
              </div>
            ) : (
              <select
                value={selectedBotId || ''}
                onChange={(e) => handleBotChange(Number(e.target.value))}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              >
                <option value="">Bir bot se√ßin...</option>
                {bots.map((bot) => (
                  <option key={bot.id} value={bot.id}>
                    {bot.bot_name} (@{bot.bot_username})
                    {bot.channel_count ? ` - ${bot.channel_count} kanal` : ''}
                  </option>
                ))}
              </select>
            )}

            {selectedBot && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <p className="text-xs text-blue-800">
                  ‚úì Se√ßili bot: <strong>{selectedBot.bot_name}</strong> (@{selectedBot.bot_username})
                </p>
              </div>
            )}
          </div>

          {/* Step 2: Kanal Username/Link */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              <Hash className="inline mr-1" size={16} />
              2. Kanal Username veya Link *
            </label>
            <input
              type="text"
              value={channelIdentifier}
              onChange={(e) => handleChannelChange(e.target.value)}
              placeholder="@kanaladim veya https://t.me/kanaladim"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            />
            <p className="text-xs text-gray-500 mt-2">
              Kanal username'ini (@kanaladim) veya davet linkini girin
            </p>

            {selectedBotId && (
              <div className="mt-3 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <p className="text-xs text-yellow-800 font-medium mb-1">
                  ‚ö†Ô∏è √ñnemli Adƒ±mlar:
                </p>
                <ol className="text-xs text-yellow-800 space-y-1 list-decimal list-inside">
                  <li>Telegram'da kanalƒ±nƒ±za gidin</li>
                  <li>Kanal ayarlarƒ±ndan "Y√∂neticiler" b√∂l√ºm√ºne gidin</li>
                  <li><strong>@{selectedBot?.bot_username}</strong> botunu y√∂netici olarak ekleyin</li>
                  <li>Bot'a <strong>mesaj g√∂nderme</strong> yetkisi verin</li>
                  <li>Bu formu doldurun ve ekleyin</li>
                </ol>
              </div>
            )}
          </div>

          {/* Step 3: Marka Se√ßimi */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              3. Marka Se√ßin *
            </label>
            <select
              value={selectedBrandId}
              onChange={(e) => setSelectedBrandId(Number(e.target.value))}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
              disabled={!selectedBotId}
            >
              <option value={0}>Bir marka se√ßin...</option>
              {brands.map((brand) => (
                <option key={brand.id} value={brand.id}>
                  {brand.name}
                </option>
              ))}
            </select>
          </div>

          {/* Ek Talimatlar */}
          {showInstructions && selectedBot && channelIdentifier.trim() && (
            <div className="p-4 bg-gradient-to-r from-orange-50 to-red-50 border-2 border-orange-300 rounded-xl">
              <h4 className="font-semibold text-orange-900 mb-3 flex items-center gap-2">
                <AlertCircle size={20} />
                üöÄ Hƒ±zlƒ± Kurulum Adƒ±mlarƒ±
              </h4>
              <div className="space-y-3">
                <div className="bg-white p-3 rounded-lg shadow-sm">
                  <p className="text-sm font-medium text-gray-900 mb-2">
                    1Ô∏è‚É£ Telegram'da kanalƒ±nƒ±za gidin
                  </p>
                  <p className="text-xs text-gray-600 ml-5">
                    {channelIdentifier.startsWith('@') ? channelIdentifier : `@${channelIdentifier}`} kanalƒ±nƒ±zƒ± a√ßƒ±n
                  </p>
                </div>

                <div className="bg-white p-3 rounded-lg shadow-sm">
                  <p className="text-sm font-medium text-gray-900 mb-2">
                    2Ô∏è‚É£ Botu y√∂netici olarak ekleyin
                  </p>
                  <div className="text-xs text-gray-600 ml-5 space-y-1">
                    <p>‚Ä¢ Kanal ayarlarƒ± ‚Üí Y√∂neticiler ‚Üí Y√∂netici Ekle</p>
                    <p className="font-mono bg-blue-50 p-1 rounded">
                      @{selectedBot.bot_username}
                    </p>
                    <p>‚Ä¢ <strong>"Mesaj G√∂nderme"</strong> yetkisini verin</p>
                  </div>
                </div>

                <div className="bg-white p-3 rounded-lg shadow-sm">
                  <p className="text-sm font-medium text-gray-900 mb-2">
                    3Ô∏è‚É£ Botu ekledikten sonra doƒürula
                  </p>
                  <p className="text-xs text-gray-600 ml-5">
                    A≈üaƒüƒ±daki "Doƒürula ve Ekle" butonuna tƒ±klayƒ±n
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex gap-3 pt-4 border-t">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              disabled={verifying}
            >
              ƒ∞ptal
            </button>
            <button
              type="submit"
              disabled={verifying || !selectedBotId || !channelIdentifier.trim() || !selectedBrandId || bots.length === 0}
              className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white rounded-lg transition-all shadow-md disabled:opacity-50 disabled:cursor-not-allowed font-medium"
            >
              {verifying ? (
                <span className="flex items-center justify-center gap-2">
                  <RefreshCw className="animate-spin" size={16} />
                  Doƒürulanƒ±yor...
                </span>
              ) : showInstructions ? (
                '‚úì Doƒürula ve Ekle'
              ) : (
                'Kanal Ekle'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DynamicChannelModal;
